class Category:
    def __init__(self, id, name):
        self.id = id
        self.name = name

def extract_section_components(application_components_metadata) -> list:
    """Returns a complete list of all section components metadata in prototype.
    list is combined from all application component metadata"""
    section_components = []
    for app_comp in application_components_metadata:
        if app_comp["name"] == "authentication" or app_comp["name"] == "Authentication":
            continue
        sec_comps = app_comp["section_components"]
        for sec_comp in sec_comps:
            
            old_attributes_data = sec_comp["attributes"]
            new_sec_comp = sec_comp
            new_attributes = []
            for attribute_data in old_attributes_data:
                if isinstance(attribute_data, dict):
                    new_attribute_data_dict = attribute_data 
                    new_attribute_data_dict["name"] = attribute_name_sanitizer(attribute_data["name"]) 
                    new_attributes.append(new_attribute_data_dict)
                elif isinstance(attribute_data, str):
                    new_attributes.append(attribute_name_sanitizer(attribute_data))
                else:
                    raise Exception("unknown format for attributes: "+ str(attribute_data))
            new_sec_comp["attributes"]= new_attributes
            section_components.append(new_sec_comp)
    return section_components

def extract_pages_of_application_component(application_component_metadata):
    """returns the pages of an application_component"""
    pages_list = []
    if "pages" in application_component_metadata:
        for page in application_component_metadata["pages"]:
            pages_list.append(page)
    else:
        raise Exception("no pages on "+ application_component_metadata["name"])
    return pages_list